'use strict';(function(){const indexCfg={cache:true};indexCfg.doc={id:'id',field:['title','content'],store:['title','href','section'],};const index=FlexSearch.create('balance',indexCfg);window.bookSearchIndex=index;index.add({'id':0,'href':'/posts/build-blog/','title':"使用Hugo和Github Pages搭建博客",'section':"Blog",'content':"本文\nGithub Pages #  按照 Github Pages官网 说明，新建一个和github用户名一样的Repo，格式为 username.github.io。\nHugo 安装 #  参考 Hugo官网 本地安装hugo，笔者Mac使用brew安装:\n$ brew install hugo ··· $ hugo version Hugo Static Site Generator v0.80.0/extended darwin/amd64 BuildDate: unknown Site 初始化 #  $ hugo new site malfurionpd.github.io cd malfurionpd.github.io git init 主题安装 #  hugo-book\ngit submodule add https://github.com/alex-shpak/hugo-book themes/book cp -R themes/book/exampleSite/content . hugo server --minify --theme book 配置 #  docs\n域名\n发布 #  hugo new site malfurionpd.github.io cd malfurionpd.github.io echo \u0026quot;# malfurionpd.github.io\u0026quot; \u0026gt;\u0026gt; README.md git init git add README.md git commit -m \u0026quot;first commit\u0026quot; git branch -M main git remote add origin git@github.com:malfurionpd/adenghub.github.io.git git push -u origin main Refs #  GitHub Pages 搭建教程\n"});})();